## 2. Create a widget provider class

```kotlin:app/src/main/java/com/devrachit/ken/widget/LeetCodeWidget.kt
package com.devrachit.ken.widget

import android.app.PendingIntent
import android.appwidget.AppWidgetManager
import android.appwidget.AppWidgetProvider
import android.content.Context
import android.content.Intent
import android.graphics.Color
import android.os.Handler
import android.os.Looper
import android.view.View
import android.widget.RemoteViews
import com.devrachit.ken.MainActivity
import com.devrachit.ken.R
import com.devrachit.ken.api.LeetCodeApiService
import com.devrachit.ken.data.LeetCodeRepository
import kotlinx.coroutines.CoroutineScope
import kotlinx.coroutines.Dispatchers
import kotlinx.coroutines.SupervisorJob
import kotlinx.coroutines.launch
import java.time.LocalDate
import java.time.format.DateTimeFormatter
import java.time.temporal.ChronoUnit
import java.util.concurrent.Executors

class LeetCodeWidget : AppWidgetProvider() {

 private val job = SupervisorJob()
 private val coroutineScope = CoroutineScope(Dispatchers.IO + job)
 private val mainHandler = Handler(Looper.getMainLooper())

 override fun onUpdate(
 context: Context,
 appWidgetManager: AppWidgetManager,
 appWidgetIds: IntArray
 ) {
 // Update each widget instance
 appWidgetIds.forEach { widgetId ->
 updateWidget(context, appWidgetManager, widgetId)
 }
 }

 private fun updateWidget(context: Context, appWidgetManager: AppWidgetManager, widgetId: Int) {
 // Initialize RemoteViews for the widget layout
 val views = RemoteViews(context.packageName, R.layout.leetcode_widget_layout)

 // Set up click intent to open the app
 val intent = Intent(context, MainActivity::class.java)
 val pendingIntent = PendingIntent.getActivity(
 context,
 0,
 intent,
 PendingIntent.FLAG_IMMUTABLE
 )
 views.setOnClickPendingIntent(R.id.widget_container, pendingIntent)

 // Set initial loading state
 views.setViewVisibility(R.id.progress_bar, View.VISIBLE)
 views.setViewVisibility(R.id.heatmap_grid, View.GONE)
 views.setTextViewText(R.id.widget_title, "Loading LeetCode data...")

 // Update the widget initially with loading state
 appWidgetManager.updateAppWidget(widgetId, views)

 // Start fetching data
 fetchData(context, appWidgetManager, widgetId)
 }

 private fun fetchData(context: Context, appWidgetManager: AppWidgetManager, widgetId: Int) {
 coroutineScope.launch {
 try {
 val repository = LeetCodeRepository(LeetCodeApiService.create())
 val username = "rockyhappy" // Replace with your desired username
 val contributionsData = repository.getUserHeatmap(username)

 // Update UI on main thread
 mainHandler.post {
 updateWidgetWithData(context, appWidgetManager, widgetId, username, contributionsData)
 }
 } catch (e: Exception) {
 // Show error state
 mainHandler.post {
 val views = RemoteViews(context.packageName, R.layout.leetcode_widget_layout)
 views.setViewVisibility(R.id.progress_bar, View.GONE)
 views.setTextViewText(R.id.widget_title, "Error loading data")
 appWidgetManager.updateAppWidget(widgetId, views)
 }
 }
 }
 }

 private fun updateWidgetWithData(
 context: Context,
 appWidgetManager: AppWidgetManager,
 widgetId: Int,
 username: String,
 contributionsData: Map<String, Int>
 ) {
 val views = RemoteViews(context.packageName, R.layout.leetcode_widget_layout)

 // Update title
 views.setViewVisibility(R.id.progress_bar, View.GONE)
 views.setViewVisibility(R.id.heatmap_grid, View.VISIBLE)
 views.setTextViewText(R.id.widget_title, "LeetCode: $username")

 // Clear the grid and rebuild it
 views.removeAllViews(R.id.heatmap_grid)

 // Get max contribution for coloring
 val maxContribution = contributionsData.values.maxOrNull() ?: 0

 // Calculate dates
 val today = LocalDate.now()
 val startDate = today.minus(52, ChronoUnit.WEEKS)

 // Generate contribution cells
 generateContributionCells(context, views, contributionsData, startDate, today, maxContribution)

 // Update the widget
 appWidgetManager.updateAppWidget(widgetId, views)
 }

 private fun generateContributionCells(
 context: Context,
 views: RemoteViews,
 contributions: Map<String, Int>,
 startDate: LocalDate,
 today: LocalDate,
 maxContribution: Int
 ) {
 val formatter = DateTimeFormatter.ISO_LOCAL_DATE

 // Create the cell views
 for (row in 0 until 7) { // 7 days in a week
 for (col in 0 until 7) { // Just show 7 weeks for simplicity in widget
 val currentDate = startDate.plusWeeks(col.toLong()).with { temporal ->
 temporal.plus(row.toLong(), ChronoUnit.DAYS)
 }

 // Skip future dates
 if (currentDate.isAfter(today)) continue

 val dateString = currentDate.format(formatter)
 val contributionCount = contributions[dateString] ?: 0

 // Create cell view
 val cellId = View.generateViewId()
 val cellView = RemoteViews(context.packageName, R.layout.contribution_cell)

 // Set cell color based on contribution count
 val cellColor = getContributionColor(contributionCount, maxContribution)
 cellView.setInt(R.id.cell_background, "setBackgroundColor", cellColor)

 // Add cell to grid at position
 views.addView(R.id.heatmap_grid, cellView)
 }
 }
 }

 private fun getContributionColor(count: Int, maxCount: Int): Int {
 return when {
 count == 0 -> Color.parseColor("#EBEDF0")
 count <= maxCount / 4 -> Color.parseColor("#9BE9A8")
 count <= maxCount / 2 -> Color.parseColor("#40C463")
 count <= 3 * maxCount / 4 -> Color.parseColor("#30A14E")
 else -> Color.parseColor("#216E39")
 }
 }

 override fun onDisabled(context: Context) {
 super.onDisabled(context)
 job.cancel()
 }
}
```

