import android.content.Context
import com.chuckerteam.chucker.api.ChuckerInterceptor
import com.devrachit.ken.data.remote.model.UserExistsResponse
import com.devrachit.ken.data.remote.model.UserInfoResponse
import kotlinx.serialization.json.Json
import okhttp3.MediaType.Companion.toMediaType
import okhttp3.OkHttpClient
import okhttp3.Request
import okhttp3.RequestBody.Companion.toRequestBody
import javax.inject.Inject
import javax.inject.Singleton
import android.util.Log
import com.devrachit.ken.data.GraphqlQuery
import kotlinx.coroutines.Dispatchers
import kotlinx.coroutines.withContext

@Singleton
class LeetCodeGraphqlApi @Inject constructor(
 context: Context
) {
 private val client = OkHttpClient.Builder()
 .addInterceptor(ChuckerInterceptor.Builder(context).build())
 .build()

 private val json = Json { ignoreUnknownKeys = true }

 suspend fun checkUserExists(username: String): Result<Boolean> {
 Log.d(TAG, "Checking if user $username exists")

 val requestJson = GraphqlQuery.createUserExistsQuery(username)
 val mediaType = "application/json; charset=utf-8".toMediaType()
 val requestBody = requestJson.toRequestBody(mediaType)

 val request = Request.Builder()
 .url(GRAPHQL_ENDPOINT)
 .post(requestBody)
 .header("Content-Type", "application/json")
 .header("Referer", "https://leetcode.com/")
 .build()

 return withContext(Dispatchers.IO) {
 try {
 client.newCall(request).execute().use { response ->
 if (!response.isSuccessful) {
 Log.e(TAG, "API error: ${response.code}")
 return@withContext Result.failure(Exception("API error: ${response.code}"))
 }

 val responseBody = response.body?.string() 
 ?: return@withContext Result.failure(Exception("Empty response"))

 try {
 val userExistsResponse = json.decodeFromString<UserExistsResponse>(responseBody)
 val userExists = userExistsResponse.data?.matchedUser != null
 Result.success(userExists)
 } catch (e: Exception) {
 Log.e(TAG, "Parse error: ${e.message}", e)
 Result.failure(e)
 }
 }
 } catch (e: Exception) {
 Log.e(TAG, "Network error: ${e.message}", e)
 Result.failure(e)
 }
 }
 }

 suspend fun getUserInfo(username: String): Result<UserInfoResponse.MatchedUser> {
 Log.d(TAG, "Fetching info for user $username")

 val requestJson = GraphqlQuery.createUserInfoQuery(username)
 val mediaType = "application/json; charset=utf-8".toMediaType()
 val requestBody = requestJson.toRequestBody(mediaType)

 val request = Request.Builder()
 .url(GRAPHQL_ENDPOINT)
 .post(requestBody)
 .header("Content-Type", "application/json")
 .header("Referer", "https://leetcode.com/")
 .build()

 return withContext(Dispatchers.IO) {
 try {
 client.newCall(request).execute().use { response ->
 if (!response.isSuccessful) {
 Log.e(TAG, "API error: ${response.code}")
 return@withContext Result.failure(Exception("API error: ${response.code}"))
 }

 val responseBody = response.body?.string() 
 ?: return@withContext Result.failure(Exception("Empty response"))

 try {
 val userInfoResponse = json.decodeFromString<UserInfoResponse>(responseBody)

 if (userInfoResponse.errors != null && userInfoResponse.errors.isNotEmpty()) {
 return@withContext Result.failure(
 Exception("GraphQL error: ${userInfoResponse.errors.first().message}")
 )
 }

 val matchedUser = userInfoResponse.data?.matchedUser
 ?: return@withContext Result.failure(Exception("User not found"))

 Result.success(matchedUser)
 } catch (e: Exception) {
 Log.e(TAG, "Parse error: ${e.message}", e)
 Result.failure(e)
 }
 }
 } catch (e: Exception) {
 Log.e(TAG, "Network error: ${e.message}", e)
 Result.failure(e)
 }
 }
 }

 companion object {
 private const val TAG = "LeetCodeGraphqlApi"
 private const val GRAPHQL_ENDPOINT = "https://leetcode.com/graphql"
 }
}
```

