 @Query("DELETE FROM leetcode_users")
 suspend fun deleteAllUsers()

 @Query("SELECT * FROM leetcode_users WHERE lastFetchTime < :timestamp")
 suspend fun getExpiredCacheEntries(timestamp: Long): List<LeetCodeUserEntity>
}
