And finally, implement the repository with cache management:

```kotlin:app/src/main/java/com/devrachit/ken/data/repository/LeetCodeRepositoryImpl.kt
package com.devrachit.ken.data.repository

import com.devrachit.ken.data.local.dao.LeetCodeUserDao
import com.devrachit.ken.data.local.entity.LeetCodeUserEntity
import com.devrachit.ken.data.remote.LeetCodeRemoteDataSource
import com.devrachit.ken.domain.models.LeetCodeUserInfo
import com.devrachit.ken.domain.repository.LeetCodeRepository
import kotlinx.coroutines.flow.Flow
import kotlinx.coroutines.flow.map
import java.util.concurrent.TimeUnit

class LeetCodeRepositoryImpl(
 private val remoteDataSource: LeetCodeRemoteDataSource,
 private val localDataSource: LeetCodeUserDao
) : LeetCodeRepository {

 private val CACHE_TIMEOUT_MS = TimeUnit.HOURS.toMillis(2) // 2 hour cache validity

 override fun getUserProfile(username: String, forceRefresh: Boolean): Flow<Result<LeetCodeUserInfo>> {
 return localDataSource.getUserByUsernameFlow(username)
 .map { cachedUser ->
 if (cachedUser == null || forceRefresh || isCacheExpired(cachedUser.lastFetchTime)) {
 try {
 val remoteResult = remoteDataSource.getUserProfile(username)
 if (remoteResult.isSuccess) {
 remoteResult.getOrNull()?.let { user ->
 // Save to cache
 localDataSource.insertUser(
 LeetCodeUserEntity.fromDomainModel(user, System.currentTimeMillis())
 )
 }
 remoteResult
 } else {
 // If remote fetch failed but we have cached data, return cached data
 if (cachedUser != null) {
 Result.success(cachedUser.toDomainModel())
 } else {
 remoteResult
 }
 }
 } catch (e: Exception) {
 // If exception occurred but we have cached data, return cached data
 if (cachedUser != null) {
 Result.success(cachedUser.toDomainModel())
 } else {
 Result.failure(e)
 }
 }
 } else {
 // Return cached data
 Result.success(cachedUser.toDomainModel())
 }
 }
 }

 override suspend fun clearCache() {
 localDataSource.deleteAllUsers()
 }

 override suspend fun clearUserCache(username: String) {
 localDataSource.deleteUser(username)
 }

 override suspend fun refreshUserProfile(username: String): Result<LeetCodeUserInfo> {
 return try {
 val remoteResult = remoteDataSource.getUserProfile(username)
 if (remoteResult.isSuccess) {
 remoteResult.getOrNull()?.let { user ->
 localDataSource.insertUser(
 LeetCodeUserEntity.fromDomainModel(user, System.currentTimeMillis())
 )
 }
 }
 remoteResult
 } catch (e: Exception) {
 Result.failure(e)
 }
 }

 private fun isCacheExpired(lastFetchTime: Long): Boolean {
 return System.currentTimeMillis() - lastFetchTime > CACHE_TIMEOUT_MS
 }

 // Helper method to clean up expired cache entries
 suspend fun cleanExpiredCache() {
 val expiryTimestamp = System.currentTimeMillis() - CACHE_TIMEOUT_MS
 val expiredEntries = localDataSource.getExpiredCacheEntries(expiryTimestamp)
 expiredEntries.forEach { 
 localDataSource.deleteUser(it.username)
 }
 }
}
```

