import javax.inject.Inject

class GetUserInfoUseCase @Inject constructor(
 private val repository: LeetcodeRepository
) {
 operator fun invoke(username: String, forceRefresh: Boolean = false): Flow<Resource<LeetCodeUserInfo>> {
 return repository.getUserInfoStream(username, forceRefresh)
 }
