 .collectLatest { result ->
 when (result) {
 is Resource.Loading -> handleLoadingState()
 is Resource.Success -> handleSuccessState(result.data as LeetCodeUserInfo)
 is Resource.Error -> handleErrorState(result.message)
 }
 }
