@Composable
fun HeatMap(activityData: Map<Long, Int>) {
 val boxSize = 20.dp
 val spacing = 4.dp
 val daysInWeek = 7
 val weeks = 16 // Approximate number of weeks in 4 months

 val startDate = LocalDate.now().minusWeeks(weeks.toLong()).with(DayOfWeek.SUNDAY)

 Canvas(modifier = Modifier
 .fillMaxWidth()
 .height((boxSize + spacing) * daysInWeek)
 .padding(16.dp)) {

 for (week in 0 until weeks) {
 for (day in 0 until daysInWeek) {
 val currentDate = startDate.plusWeeks(week.toLong()).plusDays(day.toLong())
 val timestamp = currentDate.atStartOfDay(ZoneId.systemDefault()).toEpochSecond()
 val count = activityData[timestamp] ?: 0
 val color = getColorForCount(count)

 val x = week * (boxSize.toPx() + spacing.toPx())
