+ // Track current month for visual separators
+ var currentMonth = -1
+ val monthSeparators = mutableListOf<Float>()
+ 
+ for (week in 0 until weeksToShow) {
+ for (day in 0 until daysInWeek) {
+ // Calculate the date for this cell
+ val currentDate = startDate.plusWeeks(week.toLong()).plusDays(day.toLong())
+ 
+ // Skip future dates
+ if (currentDate.isAfter(LocalDate.now())) {
+ continue
+ }
+ 
+ // Add visual separator between months
+ val month = currentDate.monthValue
+ if (day == 0 && currentMonth != -1 && month != currentMonth) {
+ monthSeparators.add(week * (boxSizePx + spacingPx) - (spacingPx / 2))
+ }
+ if (day == 0) {
+ currentMonth = month
+ }
+ 
+ // Convert to timestamp (seconds)
+ val timestamp = currentDate.atStartOfDay(ZoneId.systemDefault()).toEpochSecond()
+ 
+ // Get activity count
+ val count = longActivityData[timestamp] ?: 0
+ 
+ // Calculate position
+ val x = week * (boxSizePx + spacingPx)
+ val y = day * (boxSizePx + spacingPx)
+ 
+ // Draw cell with appropriate color
+ drawRect(
+ color = getColorForActivity(count, maxActivity),
+ topLeft = Offset(x, y),
+ size = Size(boxSizePx, boxSizePx)
+ )
+ }
+ }
+ 
+ // Draw month separators
+ for (x in monthSeparators) {
+ drawLine(
+ color = Color.Gray.copy(alpha = 0.3f),
+ start = Offset(x, 0f),
+ end = Offset(x, daysInWeek * (boxSizePx + spacingPx)),
+ strokeWidth = spacingPx / 2
+ )
+ }
+ }
+ }
+ }
+ 
