 screenWidthDp >= 600 -> (screenWidthDp * 0.6f).dp.coerceAtMost(400.dp)
 screenWidthDp >= 360 -> (screenWidthDp * 1.50f).dp.coerceAtMost(600.dp)
 else -> screenWidthDp.dp.coerceAtMost(350.dp)
