 val translationX = remember {
 Animatable(0f)
 }
 translationX.updateBounds(0f, drawerWidth.value)

 // Calculate drawer progress based on translation
 val drawerProgress = (translationX.value / drawerWidth.value).coerceIn(0f, 1f)

