 fun loadUserDetails() {
 if (_hasLoadedInitialData.value) {
 // Skip loading if already loaded once in this ViewModel lifecycle
 return
 }

 // Your existing load logic
 viewModelScope.launch {
 // ... loading logic ...
 savedStateHandle["hasLoadedInitialData"] = true
 }
 }

 // Method to force refresh when needed
 fun forceRefreshUserDetails() {
 viewModelScope.launch {
 // ... loading logic without checking the flag ...
 }
 }
}
```

