@AndroidEntryPoint
class QuestionProgressWidgetReceiver : GlanceAppWidgetReceiver() {
 override val glanceAppWidget: GlanceAppWidget = QuestionProgressWidget()

 @Inject
 lateinit var getUserQuestionStatusUseCase: GetUserQuestionStatusUseCase

 @Inject
 lateinit var dataStoreRepository: DataStoreRepository

 override fun onEnabled(context: Context) {
 super.onEnabled(context)
 // Schedule the periodic work
 schedulePeriodicUpdate(context)

 // Perform an immediate update
 val updateWork = OneTimeWorkRequestBuilder<QuestionProgressUpdateWorker>()
 .build()
 WorkManager.getInstance(context).enqueue(updateWork)
 }

 private fun schedulePeriodicUpdate(context: Context) {
 val constraints = Constraints.Builder()
 .setRequiredNetworkType(NetworkType.CONNECTED)
 .build()

 val updateRequest = PeriodicWorkRequestBuilder<QuestionProgressUpdateWorker>(
 1, TimeUnit.HOURS // Update every hour
 )
 .setConstraints(constraints)
 .setBackoffCriteria(
 BackoffPolicy.LINEAR,
 30, TimeUnit.MINUTES
 )
 .build()

 WorkManager.getInstance(context)
 .enqueueUniquePeriodicWork(
 "update_leetcode_progress",
 ExistingPeriodicWorkPolicy.KEEP,
 updateRequest
 )
 }
}
