## 3. Create the Worker for Periodic Updates

```kotlin:app/src/main/java/com/devrachit/ken/widget/QuestionProgressUpdateWorker.kt
package com.devrachit.ken.widget

import android.content.Context
import androidx.glance.GlanceId
import androidx.glance.appwidget.GlanceAppWidgetManager
import androidx.glance.appwidget.state.updateAppWidgetState
import androidx.hilt.work.HiltWorker
import androidx.work.CoroutineWorker
import androidx.work.WorkerParameters
import com.devrachit.ken.data.local.datastore.DataStoreRepository
import com.devrachit.ken.domain.usecases.getUserQuestionStatus.GetUserQuestionStatusUseCase
import com.devrachit.ken.utility.NetworkUtility.Resource
import dagger.assisted.Assisted
import dagger.assisted.AssistedInject
import kotlinx.coroutines.flow.first
import timber.log.Timber
import kotlinx.coroutines.withContext
import kotlinx.coroutines.Dispatchers

@HiltWorker
class QuestionProgressUpdateWorker @AssistedInject constructor(
 @Assisted private val context: Context,
 @Assisted workerParams: WorkerParameters,
 private val getUserQuestionStatusUseCase: GetUserQuestionStatusUseCase,
 private val dataStoreRepository: DataStoreRepository
) : CoroutineWorker(context, workerParams) {

 override suspend fun doWork(): Result {
 try {
 // Get the username from DataStore
 val username = dataStoreRepository.readPrimaryUsername() ?: return Result.failure()

 // Fetch latest data
 val questionStatusResult = withContext(Dispatchers.IO) {
 getUserQuestionStatusUseCase(username, forceRefresh = true).first()
 }

 if (questionStatusResult is Resource.Success && questionStatusResult.data != null) {
 val data = questionStatusResult.data

 // Get all widget IDs for our widget
 val glanceAppWidgetManager = GlanceAppWidgetManager(context)
 val glanceIds = glanceAppWidgetManager.getGlanceIds(QuestionProgressWidget::class.java)

 // Calculate statistics from the data
 var easySolved = 0
 var mediumSolved = 0
 var hardSolved = 0
 var easyTotal = 0
 var mediumTotal = 0
 var hardTotal = 0

 // Extract data from the fetched question status
 data.matchedUser?.submitStats?.acSubmissionNum?.forEach { stat ->
 when (stat.difficulty.lowercase()) {
 "easy" -> easySolved = stat.count
 "medium" -> mediumSolved = stat.count
 "hard" -> hardSolved = stat.count
 }
 }

 data.allQuestionsCount?.forEach { count ->
 when (count.difficulty.lowercase()) {
 "easy" -> easyTotal = count.count
 "medium" -> mediumTotal = count.count
 "hard" -> hardTotal = count.count
 }
 }

 val totalSolved = easySolved + mediumSolved + hardSolved
 val totalQuestions = easyTotal + mediumTotal + hardTotal

 // Update all widget instances
 for (glanceId in glanceIds) {
 updateAppWidgetState(context, glanceId) { prefs ->
 prefs.apply {
 this[QuestionProgressWidget.KEY_EASY_SOLVED] = easySolved.toString()
 this[QuestionProgressWidget.KEY_EASY_TOTAL] = easyTotal.toString()
 this[QuestionProgressWidget.KEY_MEDIUM_SOLVED] = mediumSolved.toString()
 this[QuestionProgressWidget.KEY_MEDIUM_TOTAL] = mediumTotal.toString()
 this[QuestionProgressWidget.KEY_HARD_SOLVED] = hardSolved.toString()
 this[QuestionProgressWidget.KEY_HARD_TOTAL] = hardTotal.toString()
 this[QuestionProgressWidget.KEY_SOLVED] = totalSolved.toString()
 this[QuestionProgressWidget.KEY_TOTAL] = totalQuestions.toString()
 this[QuestionProgressWidget.KEY_USERNAME] = username
 this[QuestionProgressWidget.KEY_ATTEMPTING] = "0" // Could calculate this if needed
 }
 }

 // Update widget display
 QuestionProgressWidget().update(context, glanceId)
 }

 return Result.success()
 } else {
 Timber.e("Failed to fetch data: ${questionStatusResult.message}")
 return Result.retry()
 }
 } catch (e: Exception) {
 Timber.e(e, "Error updating widget")
 return Result.failure()
 }
 }
}
```

