 attempting = 25,
 total = 3200,
 easyTotalCount = 950,
 easySolvedCount = 800,
 mediumTotalCount = 1500,
 mediumSolvedCount = 400,
 hardTotalCount = 750,
 hardSolvedCount = 100
 ) { bitmap ->
 WidgetUtils.saveBitmapToFile(context, bitmap, QuestionProgressWidget.PROGRESS_IMAGE_NAME)
 }
 }

 // Then update the widget
 updateAll(context)
 }
 }
 }

 // This is triggered when the widget is updated
 override fun onUpdate(
 context: Context,
 appWidgetManager: android.appwidget.AppWidgetManager,
 appWidgetIds: IntArray
 ) {
 super.onUpdate(context, appWidgetManager, appWidgetIds)

 // Refresh the bitmap and widget when updated
 widgetScope.launch {
 with(QuestionProgressWidget()) {
 // Render new bitmap
 WidgetUtils.renderComposableToBitmap(
 context = context,
 widthPx = QuestionProgressWidget.WIDTH_PX,
 heightPx = QuestionProgressWidget.HEIGHT_PX,
 solved = 1300,
 attempting = 25,
 total = 3200,
 easyTotalCount = 950,
 easySolvedCount = 800,
 mediumTotalCount = 1500,
 mediumSolvedCount = 400,
 hardTotalCount = 750,
 hardSolvedCount = 100
 ) { bitmap ->
 WidgetUtils.saveBitmapToFile(context, bitmap, QuestionProgressWidget.PROGRESS_IMAGE_NAME)

