## 5. Updated Repository with Caching

```kotlin:app/src/main/java/com/devrachit/ken/data/repository/remote/LeetcodeRepositoryImpl.kt
package com.devrachit.ken.data.repository.remote

import android.util.Log
import com.devrachit.ken.data.GraphqlQuery
import com.devrachit.ken.data.local.dao.LeetCodeUserDao
import com.devrachit.ken.data.local.entity.LeetCodeUserEntity
import com.devrachit.ken.data.remote.model.UserInfoResponse
import com.devrachit.ken.domain.models.LeetCodeUserInfo
import com.devrachit.ken.domain.repository.LeetcodeRepository
import com.devrachit.ken.utility.NetworkUtility.Resource
import kotlinx.serialization.json.Json
import okhttp3.MediaType.Companion.toMediaType
import okhttp3.OkHttpClient
import okhttp3.Request
import okhttp3.RequestBody.Companion.toRequestBody
import org.json.JSONObject
import javax.inject.Inject

class LeetcodeRepositoryImpl @Inject constructor(
 private val client: OkHttpClient,
 private val leetCodeUserDao: LeetCodeUserDao,
 private val json: Json
) : LeetcodeRepository {

 private val CACHE_DURATION_MS = 24 * 60 * 60 * 1000L // 24 hours

 override suspend fun getUserInfo(username: String): Resource<LeetCodeUserInfo> {
 // Check cache first
 val cachedUser = leetCodeUserDao.getUserByUsername(username)

 // If we have a non-expired cached user, return it
 if (cachedUser != null && !isCacheExpired(cachedUser.lastFetchTime)) {
 Log.d(TAG, "Returning cached data for $username")
 return Resource.Success(cachedUser.toDomainModel())
 }

 // Otherwise fetch from network
 return fetchUserFromNetwork(username)
 }

 private suspend fun fetchUserFromNetwork(username: String): Resource<LeetCodeUserInfo> {
 Log.d(TAG, "Fetching user $username from network")

 val requestJson = GraphqlQuery.createUserInfoQuery(username)
 val mediaType = "application/json; charset=utf-8".toMediaType()
 val requestBody = requestJson.toRequestBody(mediaType)

 val request = Request.Builder()
 .url("https://leetcode.com/graphql")
 .post(requestBody)
 .header("Content-Type", "application/json")
 .header("Referer", "https://leetcode.com/")
 .build()

 try {
 client.newCall(request).execute().use { response ->
 if (!response.isSuccessful) {
 Log.e(TAG, "API error: ${response.code}")
 return Resource.Error("API error: ${response.code}")
 }

 val responseBody = response.body?.string()
 ?: return Resource.Error("Empty response body")

 try {
 // Parse the GraphQL response
 val responseJson = JSONObject(responseBody)

 // Check for GraphQL errors
 if (responseJson.has("errors")) {
 val errorMessage = responseJson.getJSONArray("errors")
 .getJSONObject(0)
 .getString("message")
 return Resource.Error("GraphQL error: $errorMessage")
 }

 // Check if user exists
 val data = responseJson.optJSONObject("data")
 val matchedUser = data?.optJSONObject("matchedUser")

 if (matchedUser == null) {
 return Resource.Error("User not found")
 }

 // Create domain model with extracted data
 val userInfo = createUserInfoFromJson(matchedUser, username)

 // Cache the data
 cacheUserInfo(userInfo)

 return Resource.Success(userInfo)

 } catch (e: Exception) {
 Log.e(TAG, "Parse error: ${e.message}", e)
 return Resource.Error("Failed to parse response: ${e.message}")
 }
 }
 } catch (e: Exception) {
 Log.e(TAG, "Network error: ${e.message}", e)
 return Resource.Error("Network error: ${e.message}")
 }
 }

 private fun createUserInfoFromJson(userJson: JSONObject, username: String): LeetCodeUserInfo {
 val firstName = userJson.optString("firstName", null)
 val lastName = userJson.optString("lastName", null)

 val profileJson = userJson.optJSONObject("profile")
 val contributionsJson = userJson.optJSONObject("contributions")

 return LeetCodeUserInfo(
 username = username,
 firstName = firstName,
 lastName = lastName,
 fullName = if (firstName != null || lastName != null) {
 listOfNotNull(firstName, lastName).joinToString(" ")
 } else null,
 avatarUrl = profileJson?.optString("userAvatar"),
 ranking = profileJson?.optInt("ranking"),
 countryName = profileJson?.optString("countryName"),
 company = profileJson?.optString("company"),
 school = profileJson?.optString("school"),
 githubUrl = userJson.optString("githubUrl"),
 twitterUrl = userJson.optString("twitterUrl"),
 linkedinUrl = userJson.optString("linkedinUrl"),
 points = contributionsJson?.optInt("points"),
 questionCount = contributionsJson?.optInt("questionCount"),
 lastFetchTime = System.currentTimeMillis()
 )
 }

 private suspend fun cacheUserInfo(userInfo: LeetCodeUserInfo) {
 val entity = LeetCodeUserEntity.fromDomainModel(userInfo)
 leetCodeUserDao.insertUser(entity)
 Log.d(TAG, "Cached user info for ${userInfo.username}")

 // Clean up old cached data
 cleanUpOldCache()
 }

 private suspend fun cleanUpOldCache() {
 val expiryTime = System.currentTimeMillis() - CACHE_DURATION_MS
 leetCodeUserDao.deleteOldData(expiryTime)
 }

 private fun isCacheExpired(timestamp: Long): Boolean {
 val age = System.currentTimeMillis() - timestamp
 return age > CACHE_DURATION_MS
 }

 companion object {
 private const val TAG = "LeetcodeRepositoryImpl"
 }
}
```

