Let's add a function to your `OnboardingViewmodel` that checks if a user exists on LeetCode using GraphQL:

```kotlin:app/src/main/java/com/devrachit/ken/presentation/screens/auth/onboarding/OnboardingViewmodel.kt
package com.devrachit.ken.presentation.screens.auth.onboarding

import androidx.lifecycle.ViewModel
import androidx.lifecycle.viewModelScope
import dagger.hilt.android.lifecycle.HiltViewModel
import kotlinx.coroutines.flow.MutableStateFlow
import kotlinx.coroutines.flow.StateFlow
import kotlinx.coroutines.flow.asStateFlow
import kotlinx.coroutines.launch
import kotlinx.serialization.SerialName
import kotlinx.serialization.Serializable
import kotlinx.serialization.json.Json
import okhttp3.MediaType.Companion.toMediaType
import okhttp3.OkHttpClient
import okhttp3.Request
import okhttp3.RequestBody.Companion.toRequestBody
import javax.inject.Inject
import kotlin.coroutines.resume
import kotlin.coroutines.resumeWithException
import kotlin.coroutines.suspendCoroutine

data class User(
 val userName: String? = "",
 val isUserNameValid: Boolean? = true,
 val isChecking: Boolean = false,
 val errorMessage: String? = null
)

@HiltViewModel
class OnboardingViewmodel @Inject constructor() : ViewModel() {
 private var _userValues = MutableStateFlow(User())
 val userValues: StateFlow<User> = _userValues.asStateFlow()

 private val client = OkHttpClient()
 private val json = Json { ignoreUnknownKeys = true }

 fun updateUserName(userName: String) {
 _userValues.value = _userValues.value.copy(userName = userName)
 }

 @Serializable
 private data class GraphQLResponse(
 val data: Data? = null,
 val errors: List<Error>? = null
 ) {
 @Serializable
 data class Data(
 @SerialName("matchedUser")
 val matchedUser: MatchedUser? = null
 )

 @Serializable
 data class MatchedUser(
 val username: String
 )

 @Serializable
 data class Error(
 val message: String
 )
 }

 fun checkUserExists() {
 val username = _userValues.value.userName

 if (username.isNullOrBlank()) {
 _userValues.value = _userValues.value.copy(
 isUserNameValid = false,
 errorMessage = "Username cannot be empty"
 )
 return
 }

 viewModelScope.launch {
 _userValues.value = _userValues.value.copy(isChecking = true, errorMessage = null)

 try {
 val userExists = checkIfUserExistsOnLeetCode(username)
 _userValues.value = _userValues.value.copy(
 isUserNameValid = userExists,
 isChecking = false,
 errorMessage = if (userExists) null else "User not found on LeetCode"
 )
 } catch (e: Exception) {
 _userValues.value = _userValues.value.copy(
 isChecking = false,
 isUserNameValid = false,
 errorMessage = "Error checking user: ${e.message}"
 )
 }
 }
 }

 private suspend fun checkIfUserExistsOnLeetCode(username: String): Boolean = suspendCoroutine { continuation ->
 // The GraphQL query to check if a user exists
 val query = """
 {
 "query": "query userPublicProfile(${'$'}username: String!) { matchedUser(username: ${'$'}username) { username } }",
 "variables": {"username": "$username"}
 }
 """.trimIndent()

 val mediaType = "application/json; charset=utf-8".toMediaType()
 val requestBody = query.toRequestBody(mediaType)

 val request = Request.Builder()
 .url("https://leetcode.com/graphql")
 .post(requestBody)
 .header("Content-Type", "application/json")
 .build()

 try {
 client.newCall(request).execute().use { response ->
 if (!response.isSuccessful) {
 continuation.resumeWithException(Exception("Network error: ${response.code}"))
 return@use
 }

 val responseBody = response.body?.string()
 if (responseBody == null) {
 continuation.resumeWithException(Exception("Empty response"))
 return@use
 }

 val graphQLResponse = json.decodeFromString<GraphQLResponse>(responseBody)

 if (graphQLResponse.errors != null && graphQLResponse.errors.isNotEmpty()) {
 continuation.resumeWithException(Exception(graphQLResponse.errors.first().message))
 return@use
 }

 // If we got a non-null matchedUser, the user exists
